
public class NestedLoop {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		for(int i=0;i<=3;i++) {
			
			System.out.println("Outer loop");
			
			   for(int j=0;j<=2;j++) {
				   
				   System.out.println("Inner loop");
			   }
			
			System.out.println("Finished Outer loop");
			   
		}

	}

}
